            new TestCase<String>()
                .addArguments(
                    "-mode", "enc",
                    "-key", "5",
                    "-data", "Welcome to hyperskill!"
                )
                .setAttach("\\jqhtrj%yt%m~ujwxpnqq&"),

            new TestCase<String>()
